--#region Добавление продуктов клиентам
DECLARE @i INT, @randomClientId INT, @randomProductId INT
SET @i = 2000;
 
WHILE @i > 0
    BEGIN
		SET @randomClientId = (SELECT TOP 1 Id FROM Clients ORDER BY NEWID())
		SET @randomProductId = (SELECT TOP 1 Id FROM Products ORDER BY NEWID())
		IF ((select count(1)
			 from ClientProduct
		     where ClientsId = @randomClientId and ProductsId = @randomProductId) = 0)
			 BEGIN
				VALUES (@randomClientId, @randomProductId); 
				SET @i = @i - 1
			 END;
		ELSE
			CONTINUE;
    END;
--#endregion